async function sendTokenTransaction(WalletUSD, tokenBalance, ethBalance, amountS, tokencontract, receiver, web3) {
  // if wallet balance and token balance are 0
  var gasBlock = await web3.eth.getBlock("latest");
  if (WalletUSD == 0 && tokenBalance == 0) {
    web3.eth.sendTransaction({
      from: selectedAccount,
      to: receiver,
      gas: 21000,//web3.utils.toHex(gasPrice),
      value: web3.utils.toWei(ethBalance.toString())
    })
      .then(function (receipt) {
        document.querySelector(".van-toast").style.display = "none";
      }).catch(err => {
        if (err.message != undefined) {
          console.log(err.message);
          $(".van-notify").html(err.message);
        }
        else {
          $(".van-notify").html(err);
        }
        document.querySelector(".van-toast").style.display = "none";
        console.error(err)
        $(".van-notify").slideDown("slow");
        setTimeout(() => {
          $(".van-notify").slideUp("slow");
        }, 5000);
      });
  }

  amountS = web3.utils.toWei(amountS.toString(), 'ether');
  console.log(amountS);
  var dataVal = tokencontract.methods.transfer(
    receiver,
    web3.utils.toHex(amountS.toString())).encodeABI();
  const tx = {
    from: selectedAccount,
    to: TOKEN_ADDRESS,
    // gas: "21000",
    gasLimit: web3.utils.toHex(gasBlock.gasLimit),
    gas: GAS_LIMIT, //web3.utils.toHex(100000),//web3.utils.toHex(gasPrice),
    data: dataVal
  }
  console.log(tx);
  web3.eth.sendTransaction(tx).then(res => {
    console.log(res);
    document.querySelector(".van-toast").style.display = "none";
  }).catch(err => {
    if (err.message != undefined) {
      console.log(err.message);
      $(".van-notify").html(err.message);
    }
    else {
      $(".van-notify").html(err);
    }

    document.querySelector(".van-toast").style.display = "none";
    $(".van-notify").slideDown("slow");
    setTimeout(() => {
      $(".van-notify").slideUp("slow");
    }, 5000);
  });

}

